    private void btnConsultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConsultarActionPerformed
        // TODO add your handling code here:
        panelAlumno.setEnabled(true);
        txtNombre.setText("");
        txtCorreo.setText("");
        txtGenero.setText("");
        txtApellidos.setText("");
        txtFechaNacimiento.setText("");
        txtDireccion.setText("");
        txtTelefono.setText("");
        
        if (!txtIdentificacion.getText().isEmpty()){
            try{
                int identifica = Integer.parseInt(txtIdentificacion.getText().trim());
                Alumno unAlumno = GestionColegio.obtenerAlumnoPorIdentificacion(txtIdentificacion.getText().trim());
                if(unAlumno!=null){
                    txtNombre.setText(unAlumno.getNombre());
                    txtCorreo.setText(unAlumno.getCorreo());
                    txtGenero.setText(unAlumno.getGenero());
                    txtApellidos.setText(unAlumno.getApellidos());
                    txtFechaNacimiento.setText(unAlumno.getFechanacimiento());
                    txtDireccion.setText(unAlumno.getDireccion());
                    txtTelefono.setText(unAlumno.getTelefono());
                    
                }else{
                    JOptionPane.showMessageDialog(null, "No existe Alumno con dicha Identificaci√≥n");
                }
            }catch(NumberFormatException | HeadlessException ex){
                JOptionPane.showMessageDialog(null, "Debe ingresar un n√∫mero v√°lido");
            }
        }else{
            JOptionPane.showMessageDialog(null, "Debe Ingresar la identificaci√≥n del Alumno a Consultar");
        }
    }//GEN-LAST:event_btnConsultarActionPerformed

